sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
g_form.addInfoMessage('OldValue: '+ oldValue);
	g_form.addInfoMessage('newValue: '+ newValue);

	try{
		var ga = new GlideAjax('ChildcareCentreVacancies');
		ga.addParam('sysparm_name', 'getChildcareVacancies');		
		ga.addParam('sysparm_centre_id', newValue);
		ga.getXML(ChildcareVacenciesParse);
	}
	catch(ex){
		alert(ex);
	}

	function ChildcareVacenciesParse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		alert(answer); }
	}
